# ---------------- UI BLOCKS ----------------
def show_theory(text):
    st.subheader("üìñ –¢–µ–æ—Ä–∏—è")
    st.info(text)

def show_lab(lab_data):
    st.subheader("üî¨ –ó–µ—Ä—Ç—Ö–∞–Ω–∞–ª—ã“õ –∂“±–º—ã—Å")
    st.write(f"**–†–µ–∞–∫—Ç–∏–≤—Ç–µ—Ä:** {lab_data['reactive']}")
    st.write(f"**–ë–∞“õ—ã–ª–∞—É:** {lab_data['observation']}")
    st.write(f"**“ö–æ—Ä—ã—Ç—ã–Ω–¥—ã:** {lab_data['conclusion']}")
    st.write("–ê–Ω–∏–º–∞—Ü–∏—è–ª–∞–Ω“ì–∞–Ω —Ç”ô–∂—ñ—Ä–∏–±–µ:")
    
    progress_text = "–ü—Ä–æ–≥—Ä–µ—Å—Å:"
    my_bar = st.progress(0)
    for percent_complete in range(101):
        time.sleep(0.01)
        my_bar.progress(percent_complete, text=progress_text)
    st.success("–¢”ô–∂—ñ—Ä–∏–±–µ –∞—è“õ—Ç–∞–ª–¥—ã!")

def show_ai(task):
    st.subheader("ü§ñ AI-—Å–∏–º—É–ª—è—Ü–∏—è —Ç–∞–ø—Å—ã—Ä–º–∞—Å—ã")
    user_answer = st.text_area("AI –∂–∞—É–∞–±—ã / –æ–π—ã“£–¥—ã –∂–∞–∑")
    if st.button("AI-–¥–∞–Ω “Ø–ª–≥—ñ –∂–∞—É–∞–ø"):
        st.success(task)

def show_test(test_items):
    st.subheader("üìù –¢–µ—Å—Ç")
    score = 0
    for idx, (q, opts, correct) in enumerate(test_items):
        ans = st.radio(f"{idx+1}. {q}", opts, index=None, key=f"{lesson_number}_{idx}")
        if ans and opts.index(ans) == correct:
            score += 1
    st.write(f"‚úÖ “∞–ø–∞–π: {score} / {len(test_items)}")

